                        const icaObserver = new IntersectionObserver(function(entries, observer) {
                            entries.forEach(function(entry) {
                                if (entry.isIntersecting) {
                                    icaAnimation();
                                    icaObserver.disconnect()
                                }
                            })
                        }, {
                            threshold: 0.5
                        });
                        icaGraphics.forEach(function(icaGraphic) {
                            icaObserver.observe(icaGraphic)
                        });

                        function icaAnimation() {
                            target = $('.ica-graphic');
                            target.addClass('animate');
                            window.setTimeout(function() {
                                target.addClass('animation-end').removeClass('animate')
                            }, 4600)
                        }
